<div class="panel panel-default"> <div class="panel-heading"> <?php echo app('translator')->getFromJson('View User'); ?> </div> <div class="panel-body"> <div class="table-responsive"> <table class="display table table-striped table-bordered table-hover" id="users-table"> <thead> <tr> <th><?php echo app('translator')->getFromJson('S.No.'); ?></th> <th><?php echo app('translator')->getFromJson('Name'); ?></th> <th><?php echo app('translator')->getFromJson('Phone'); ?></th> <th><?php echo app('translator')->getFromJson('Email'); ?></th> <th><?php echo app('translator')->getFromJson('Role'); ?></th> <th><?php echo app('translator')->getFromJson('Status'); ?></th> <th><?php echo app('translator')->getFromJson('Created'); ?></th> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('update', App\User::class)): ?> <th><?php echo app('translator')->getFromJson('Edit'); ?></th> <?php endif; ?> </tr> </thead> <tbody> <?php if($users): ?> <?php 
                    $i = 1;
                ?> <?php $__currentLoopData = $users; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $user): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?> <tr> <td> <?php echo e($i++); ?> </td> <td><?php echo e($user->name); ?></td> <td> <?php if($user->mobile): ?> <?php if($user->mobile->verified): ?> <?php echo e($user->mobile->number); ?> <strong class="text-success">(<?php echo app('translator')->getFromJson('verified'); ?>)</strong> <?php else: ?> <?php echo e($user->mobile->number); ?> <strong class="text-danger">(<?php echo app('translator')->getFromJson('unverified'); ?>)</strong> <?php endif; ?> <?php else: ?> - <?php endif; ?> </td> <td><?php echo e($user->email); ?></td> <td> <?php if($user->role): ?> <?php if($user->role->id == 1): ?> <strong><span class="text-danger"><?php echo e($user->role->name); ?></span></strong> <?php else: ?> <?php echo e($user->role->name); ?> <?php endif; ?> <?php else: ?> <?php echo app('translator')->getFromJson('None'); ?> <?php endif; ?> </td> <td> <?php if($user->is_active == '1'): ?> <span class="badge bg-success"><i class="fa fa-check"></i></span> <?php else: ?> <span class="badge bg-danger"><i class="fa fa-times"></i></span> <?php endif; ?> </td> <td><?php echo e($user->created_at ? $user->created_at : '-'); ?></td> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('update', App\User::class)): ?> <td> <a href="<?php echo e(route('manage.users.edit', $user->id)); ?>" class="btn btn-info btn-sm"> <i class="fa fa-pencil"></i> </a> <a class="btn btn-danger btn-sm" href="<?php echo e(route('manage.users.deleteuser', $user->id)); ?>" onclick="return confirm('Are you sure you want to delete this user? All data related to this user will also be deleted.')"> <i class="fa fa-trash-o"></i> </a> </td> <?php endif; ?> </tr> <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?> <?php endif; ?> </tbody> </table> </div> </div> </div>