 <div class="row"> <div class="col-xs-12 col-sm-8"> <?php if(Session::has('page_created')): ?> <p class="alert <?php echo e(Session::get('alert-class', 'alert-success')); ?>"><?php echo e(Session::get('page_created')); ?></p> <?php endif; ?> <?php if(Session::has('page_updated')): ?> <p class="alert <?php echo e(Session::get('alert-class', 'alert-success')); ?>"><?php echo e(Session::get('page_updated')); ?></p> <?php endif; ?> <?php echo $__env->make('includes.form_errors', array_except(get_defined_vars(), array('__data', '__path')))->render(); ?> <?php if(isset($certifications)): ?> <?php echo Form::model($certifications, ['method'=>'patch', 'action'=>['ManagePagesController@updateCertifications', $certifications->id], 'files'=>true,'id'=>'productForm', 'onsubmit'=>'submit_button.value = "' . __('Please Wait...') . '"; return true;']); ?> <?php else: ?> <?php echo Form::open(['method'=>'post', 'action'=>'ManagePagesController@saveCertifications', 'onsubmit'=>'submit_button.disabled = true; submit_button.value = "' . __('Please Wait...') . '"; return true;']); ?> <?php endif; ?> <div class="form-group<?php echo e($errors->has('title') ? ' has-error' : ''); ?>"> <?php echo Form::label('title', __('Page Title:')); ?> <?php echo Form::text('title', isset($certifications->title) ? $certifications->title : null, ['class'=>'form-control', 'placeholder'=>__('Enter page title')]); ?> </div> <div class="form-group<?php echo e($errors->has('content') ? ' has-error' : ''); ?>"> <?php echo Form::label('content', __('Content:')); ?> <?php echo Form::textarea('content', isset($certifications->content) ? $certifications->content : null, ['class'=>'form-control', 'placeholder'=>__('Enter page content')]); ?> </div> <div class="form-group"> <?php echo Form::label('is_active', __('Status:')); ?> <?php echo Form::select('is_active', [0=>__('inactive'), 1=>__('active')], isset($certifications->is_active) ? $certifications->is_active : '', ['class'=>'form-control selectpicker', 'data-style'=>'btn-default']); ?> </div> <div class="form-group"> <?php if(isset($certifications)): ?> <?php echo Form::submit(__('Update'), ['class'=>'btn btn-primary btn-block', 'name'=>'submit_button']); ?> <?php else: ?> <?php echo Form::submit(__('Add'), ['class'=>'btn btn-primary btn-block', 'name'=>'submit_button']); ?> <?php endif; ?> </div> <?php echo Form::close(); ?> </div> </div>