<div class="row"> <div class="col-xs-12 col-sm-8 col-md-6"> <?php if(session()->has('user_updated_error')): ?> <div class="alert alert-danger alert-dismissible" role="alert"> <button type="button" class="close" data-dismiss="alert" aria-label="Close"> <span aria-hidden="true">&times;</span> </button> <strong><?php echo e(session('user_updated_error')); ?></strong> </div> <?php endif; ?> <?php if(session()->has('user_updated')): ?> <div class="alert alert-success alert-dismissible" role="alert"> <button type="button" class="close" data-dismiss="alert" aria-label="Close"> <span aria-hidden="true">&times;</span> </button> <strong><?php echo e(session('user_updated')); ?></strong> </div> <?php endif; ?> <?php echo $__env->make('includes.form_errors', array_except(get_defined_vars(), array('__data', '__path')))->render(); ?> <?php echo Form::model($user, ['method'=>'patch', 'action'=>['ManageUsersController@update', $user->id], 'files'=>true, 'onsubmit'=>'submit_button.disabled = true; submit_button.value = "' . __('Please Wait...') . '"; return true;']); ?> <div class="form-group<?php echo e($errors->has('name') ? ' has-error' : ''); ?>"> <?php echo Form::label('name', __('Name:')); ?> <em style="color:red;">*</em> <?php echo Form::text('name', null, ['class'=>'form-control', 'placeholder'=>__('Enter name'), 'required']); ?> </div> <?php if(config('settings.phone_otp_verification')): ?> <div class="form-group<?php echo e($errors->has('phone_number') ? ' has-error' : ''); ?>"> <?php echo Form::label('phone-number',__('Phone:')); ?><em style="color:red;">*</em> <?php if(isset($user->mobile)): ?> <?php if(isset($user->mobile->verified) && $user->mobile->verified=='1'): ?> <strong class="text-success"><?php echo app('translator')->getFromJson('verified'); ?></strong> <?php else: ?> <strong class="text-danger"><?php echo app('translator')->getFromJson('unverified'); ?></strong> <?php endif; ?> <?php endif; ?> <?php echo Form::text('phone_number', isset($user->mobile) ? $user->mobile->number : null, ['class'=>'form-control phone_number','pattern'=>"[789][0-9]{9}", 'maxlength'=>"10", 'placeholder'=>__('Enter phone number')]); ?> </div> <?php endif; ?> <div class="form-group<?php echo e($errors->has('email') ? ' has-error' : ''); ?>"> <?php echo Form::label('email', __('Email:')); ?> <em style="color:red;">*</em> <?php echo Form::email('email', null, ['class'=>'form-control', 'placeholder'=>__('Enter email')]); ?> </div> <?php if($user->id !== 1): ?> <div class="form-group"> <?php echo Form::label('role', __('Role:')); ?> <?php echo Form::select('role', $roles , $user->role_id, ['class'=>'form-control selectpicker', 'data-style'=>'btn-default']); ?> </div> <?php if(!$user->role): ?> <div class="checkbox"> <label for="remove_staff"><input type="checkbox" name="remove_staff" id="remove_staff"><strong class="text-danger"><?php echo app('translator')->getFromJson('Remove Staff'); ?></strong></label> </div> <?php endif; ?> <div class="form-group"> <?php echo Form::label('verified', __('Email/Phone Status:')); ?> <?php echo Form::select('verified', [true=>__('verified'), false=>__('unverified')], null, ['class'=>'form-control selectpicker', 'data-style'=>'btn-default']); ?> </div> <div class="form-group"> <?php echo Form::label('status', __('Status:')); ?> <?php echo Form::select('status', [0=>__('inactive'), 1=>__('active')], $user->is_active, ['class'=>'form-control selectpicker', 'data-style'=>'btn-default']); ?> </div> <?php endif; ?> <div class="form-group<?php echo e($errors->has('password') ? ' has-error' : ''); ?>"> <?php echo Form::label('password', __('Password:')); ?> <em style="color:red;">*</em> <?php echo Form::password('password', ['class'=>'form-control', 'placeholder'=>__('Enter password')]); ?> </div> <div class="form-group<?php echo e($errors->has('password') ? ' has-error' : ''); ?>"> <?php echo Form::label('password_confirmation', __('Confirm Password:')); ?> <em style="color:red;">*</em> <?php echo Form::password('password_confirmation', ['class'=>'form-control', 'placeholder'=>__('Enter password again')]); ?> </div> <div class="form-group"> <?php echo Form::submit(__('Update User'), ['class'=>'btn btn-primary btn-block', 'name'=>'submit_button']); ?> </div> <?php echo Form::close(); ?> </div> </div>