<div class="row"> <div class="col-xs-12 col-sm-8"> <?php if(session()->has('page_updated')): ?> <div class="alert alert-success alert-dismissible" role="alert"> <button type="button" class="close" data-dismiss="alert" aria-label="Close"> <span aria-hidden="true">&times;</span> </button> <strong><?php echo e(session('page_updated')); ?></strong> <a target="_blank" href="<?php echo e(route('front.page.show', $page->slug)); ?>"><?php echo app('translator')->getFromJson('View Page'); ?></a> </div> <?php endif; ?> <?php echo $__env->make('includes.form_errors', array_except(get_defined_vars(), array('__data', '__path')))->render(); ?> <?php echo Form::model($page, ['method'=>'patch', 'action'=>['ManagePagesController@update', $page->id], 'onsubmit'=>'submit_button.disabled = true; submit_button.value = "' . __('Please Wait...') . '"; return true;']); ?> <div class="form-group<?php echo e($errors->has('type') ? ' has-error' : ''); ?>"> <?php echo Form::label('type', __('Type:')); ?> <?php echo Form::select('type', ['1' => 'Company', '2' => 'JewelNidhi', '3'=>'Customer care'], old('type'), ['class'=>'form-control']); ?> </div> <div class="form-group<?php echo e($errors->has('title') ? ' has-error' : ''); ?>"> <?php echo Form::label('title', __('Title:')); ?> <?php echo Form::text('title', null, ['class'=>'form-control', 'placeholder'=>__('Enter page title'), 'required']); ?> </div> <div class="form-group<?php echo e($errors->has('content') ? ' has-error' : ''); ?>"> <?php echo Form::label('content', __('Content:')); ?> <?php echo Form::textarea('content', null, ['class'=>'form-control', 'placeholder'=>__('Enter page content')]); ?> </div> <div class="form-group"> <?php echo Form::label('status', __('Status:')); ?> <?php echo Form::select('status', [0=>__('inactive'), 1=>__('active')], $page->is_active, ['class'=>'form-control selectpicker', 'data-style'=>'btn-default']); ?> </div> <?php echo $__env->make('partials.manage.meta-fields', array_except(get_defined_vars(), array('__data', '__path')))->render(); ?> <div class="form-group"> <?php echo Form::submit(__('Update'), ['class'=>'btn btn-primary col-xs-6 col-sm-6 pull-left', 'name'=>'submit_button']); ?> </div> <?php echo Form::close(); ?> </div> </div>