<div class="row"> <div class="col-xs-12 col-sm-8"> <?php echo $__env->make('includes.form_errors', array_except(get_defined_vars(), array('__data', '__path')))->render(); ?> <?php if(isset($pincode)): ?> <?php echo e(Form::model($pincode, ['route' => ['manage.settings.update_pincode', $pincode->id],'method' => 'PUT','files'=>true, 'onsubmit'=>'submit_button.disabled = true; submit_button.value = "' . __('Please Wait...') . '"; return true;'])); ?> <?php else: ?> <?php echo e(Form::open(array('route' => 'manage.settings.store_pincode','files'=>true, 'onsubmit'=>'submit_button.disabled = true; submit_button.value = "' . __('Please Wait...') . '"; return true;'))); ?> <?php endif; ?> <div class="form-group<?php echo e($errors->has('name') ? ' has-error' : ''); ?>"> <?php echo Form::label('name', __('Pincode:')); ?> <?php echo Form::text('name', isset($pincode->name) ? $pincode->name : '' , ['class'=>'form-control', 'placeholder'=>__("Enter Pincode"), 'required']); ?> </div> <div class="form-group<?php echo e($errors->has('is_active') ? ' has-error' : ''); ?>"> <?php echo Form::label('is_active', __('Status:')); ?> <?php echo Form::select('is_active', [ '1' => 'Active','2' => 'Inactive'], isset($pincode->is_active) ? $pincode->is_active : '', ['class'=>'form-control','placeholder' => 'Select Status']); ?> </div> <div class="form-group"> <?php if(isset($pincode)): ?> <?php echo Form::submit(__('Update Pincode'), ['class'=>'btn btn-primary btn-block', 'name'=>'submit_button']); ?> <?php else: ?> <?php echo Form::submit(__('Add Pincode'), ['class'=>'btn btn-primary btn-block', 'name'=>'submit_button']); ?> <?php endif; ?> </div> <?php echo Form::close(); ?> </div> </div>