<div class="panel panel-default"> <div class="panel-heading"> <?php echo app('translator')->getFromJson('View Pages'); ?> </div> <div class="panel-body"> <form id="delete-form" action="delete/pages" method="post" class="form-inline"> <div class="row"> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Page::class)): ?> <?php echo e(csrf_field()); ?> <div class="col-md-4"> <div class="text-muted"> <label for="checkboxArray"><?php echo app('translator')->getFromJson('Bulk Options'); ?> <i class="fa fa-cog" aria-hidden="true"></i></label> </div> <input type="hidden" name="_method" value="DELETE"> <div class="form-group"> <select name="checkboxArray" class="form-control"> <option value=""><?php echo app('translator')->getFromJson('Delete'); ?></option> </select> </div> <div class="form-group"> <input id="delete_all" name="" class="btn fa btn-warning" value="&#xf1d8;" onclick=" if(confirm('<?php echo app('translator')->getFromJson('Are you sure you want to delete selected pages?'); ?>')) { $('#delete_all').attr('name', 'delete_all'); event.preventDefault(); $('#delete-form').submit(); } else { event.preventDefault(); } " > </div> </div> <?php endif; ?> <div class="advanced-search col-md-<?php echo e(Auth::user()->can('delete', App\Page::class) ? '8' : '8 col-md-offset-4'); ?>"> <div class="text-muted"> <label for="checkboxArray"><?php echo app('translator')->getFromJson('Advanced Search'); ?> <i class="fa fa-search" aria-hidden="true"></i></label> </div> <div class="row"> <div class="col-md-5"> <select class="form-control" id="select-column"> <option value=<?php echo e(Auth::user()->can('delete', App\Page::class) ? 2 : 1); ?>><?php echo app('translator')->getFromJson('Title'); ?></option> <option value=<?php echo e(Auth::user()->can('delete', App\Page::class) ? 3 : 2); ?>><?php echo app('translator')->getFromJson('Status'); ?></option> <option value=<?php echo e(Auth::user()->can('delete', App\Page::class) ? 4 : 3); ?>><?php echo app('translator')->getFromJson('Created'); ?></option> </select> </div> <div class="col-md-7"> <input class="form-control" type="text" id="search-by-column" placeholder="<?php echo app('translator')->getFromJson('Search by Column'); ?>"> </div> </div> </div> </div> <div class="table-responsive"> <div class="col-md-12 text-right"> <span class="advanced-search-toggle"><?php echo app('translator')->getFromJson('Advanced Search'); ?></span> </div> <table class="display table table-striped table-bordered table-hover" id="pages-table"> <thead> <tr> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Page::class)): ?> <th><input type="checkbox" id="options"></th> <?php endif; ?> <th><?php echo app('translator')->getFromJson('ID'); ?></th> <th><?php echo app('translator')->getFromJson('Title'); ?></th> <th><?php echo app('translator')->getFromJson('Created'); ?></th> <th><?php echo app('translator')->getFromJson('Action'); ?></th> </tr> </thead> <tbody> <?php if($pages): ?> <?php $__currentLoopData = $pages; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $page): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?> <tr> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Page::class)): ?> <td><input class="checkboxes" type="checkbox" name="checkboxArray[]" value="<?php echo e($page->id); ?>"></td> <?php endif; ?> <td><?php echo e($page->id); ?></td> <td><?php echo e($page->title); ?></td> <td><?php echo e($page->created_at); ?></td> <td> <a target="_blank" href="<?php echo e(route('front.page.show', $page->slug)); ?>"> <span class="glyphicon glyphicon-search" aria-hidden="true"></span> </a>&nbsp; <?php if((Auth::user()->can('update', App\Page::class)) || (Auth::user()->can('delete', App\Page::class))): ?> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('update', App\Page::class)): ?> <a href="<?php echo e(route('manage.pages.edit', $page->id)); ?>"> <span class="glyphicon glyphicon-edit"></span> </a> <?php endif; ?> &nbsp; <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Page::class)): ?> <input type="hidden" id="delete_single" name=""> <a href="" onclick=" if(confirm('<?php echo app('translator')->getFromJson('Are you sure you want to delete this?'); ?>')) { $('#delete_single').attr('name', 'delete_single').val(<?php echo e($page->id); ?>); event.preventDefault(); $('#delete-form').submit(); } else { event.preventDefault(); } " ><span class="glyphicon glyphicon-trash text-danger"></span></a> <?php endif; ?> <?php endif; ?> </td> </tr> <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?> <?php endif; ?> </tbody> </table> </div> </form> </div> </div>