<div class="row"> <div class="col-xs-12 col-sm-8 col-md-6"> <?php if(session()->has('price_updated')): ?> <div class="alert alert-success alert-dismissible" role="alert"> <button type="button" class="close" data-dismiss="alert" aria-label="Close"> <span aria-hidden="true">&times;</span> </button> <strong><?php echo e(session('price_updated')); ?></strong> </div> <?php endif; ?> <?php echo $__env->make('includes.form_errors', array_except(get_defined_vars(), array('__data', '__path')))->render(); ?> <?php echo Form::model($pricesetting, ['method'=>'patch', 'action'=>['ManageSettingsController@updatePriceSetting'], 'files'=>true, 'onsubmit'=>'submit_button.disabled = true; submit_button.value = "' . __('Please Wait...') . '"; return true;']); ?> <div class="form-group" style="text-align:center;"> <?php echo Form::label('gold_rate', __('Gold Price:')); ?> </div> <div class="form-group"> <?php if($metalPurity): ?> <?php $__currentLoopData = $metalPurity; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $k => $price_setting): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?> <?php $prorityCarat = str_replace(' ','_',$price_setting->name) ?> <div class="col-sm-3"> <div class="form-group"> <?php echo Form::label('gold_rate', __($price_setting['name'])); ?> <?php echo Form::text($price_setting->name,array_has($goldpricesetting, $prorityCarat) ? $goldpricesetting[$prorityCarat] : 0.00, ['class'=>'form-control', 'placeholder'=>__('Enter Price')]); ?> </div> </div> <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?> <?php endif; ?> </div> <div class="form-group " style="text-align:center;"> <?php echo Form::label('silver_rate', __('Silver Price:')); ?> </div> <div class="form-group"> <div class="col-sm-6"> <div class="form-group"> <?php echo Form::label('silver_jewellery_rate', __('Silver Jewellery:')); ?> <?php echo Form::text('silver_jewellery_rate',array_has($pricesetting, 'silver_jewellery_rate') ? $pricesetting['silver_jewellery_rate'] : null,['class'=>'form-control', 'placeholder'=>__('Enter Silver Jewellery Price')]); ?> </div> </div> <div class="col-sm-6"> <div class="form-group"> <?php echo Form::label('silver_item_rate', __('Silver Item:')); ?> <?php echo Form::text('silver_item_rate',array_has($pricesetting, 'silver_item_rate') ? $pricesetting['silver_item_rate'] : null,['class'=>'form-control', 'placeholder'=>__('Enter Silver Item Price')]); ?> </div> </div> </div> <div class="form-group " style="text-align:center;"> <?php echo Form::label('platinum_rate', __('Platinum Price:')); ?> </div> <div class="form-group"> <div class="col-sm-6"> <div class="form-group"> <?php echo Form::label('platinum_rate', __('750KT:')); ?> <?php echo Form::text('750_KT',array_has($platiumpricesetting, 'platinum_seventy_five') ? $platiumpricesetting['platinum_seventy_five'] : null,['class'=>'form-control', 'placeholder'=>__('Enter Platinum Price')]); ?> </div> </div> <div class="col-sm-6"> <div class="form-group"> <?php echo Form::label('platinum_rate', __('950KT:')); ?> <?php echo Form::text('950_KT',array_has($platiumpricesetting, 'platinum_nigntifive_five') ? $platiumpricesetting['platinum_nigntifive_five'] : null,['class'=>'form-control', 'placeholder'=>__('Enter Platinum Price')]); ?> </div> </div> </div> <div class="form-group"> <?php echo Form::submit(__('Update Price'), ['class'=>'btn btn-primary btn-block', 'name'=>'submit_button']); ?> </div> </div> <?php echo Form::close(); ?> </div> </div>