<div class="row"> <div class="col-xs-12 col-sm-8"> <?php echo $__env->make('includes.form_errors', array_except(get_defined_vars(), array('__data', '__path')))->render(); ?> <?php echo Form::open(['method'=>'post', 'action'=>'ManageCouponsController@store', 'onsubmit'=>'submit_button.disabled = true; submit_button.value = "' . __('Please Wait...') . '"; return true;']); ?> <div class="form-group<?php echo e($errors->has('name') ? ' has-error' : ''); ?>"> <?php echo Form::label('name', __('Coupon Name:')); ?> <?php echo Form::text('name', null, ['class'=>'form-control', 'placeholder'=>__('Enter coupon name'), 'required']); ?> </div> <div class="form-group<?php echo e($errors->has('code') ? ' has-error' : ''); ?>"> <?php echo Form::label('code', __('Coupon Code:')); ?> <?php echo Form::text('code', null, ['class'=>'form-control', 'placeholder'=>__('Enter coupon code'), 'required']); ?> </div> <div class="form-group<?php echo e($errors->has('description') ? ' has-error' : ''); ?>"> <?php echo Form::label('description', __('Description:')); ?> <?php echo Form::textarea('description', null, ['class'=>'form-control', 'placeholder'=>__('Enter description')]); ?> </div> <div class="form-group<?php echo e($errors->has('type') ? ' has-error' : ''); ?>"> <?php echo Form::label('type', __('Discount:')); ?>&nbsp; Flat Rate : <?php echo Form::radio('type', '0', true, ['id'=>'flat_rate','class'=>'discountRadio'] ); ?>&nbsp;&nbsp; Percentage : <?php echo Form::radio('type', '1', false, ['id'=>'percentage','class'=>'discountRadio'] ); ?> </div> <div class="form-group<?php echo e($errors->has('discount_percentage') ? ' has-error' : ''); ?>" id="discount_percentage"> <?php echo Form::label('discount_percentage', __('Discount Percent:')); ?> <?php echo Form::number('discount_percentage', null, ['class'=>'form-control', 'placeholder'=>__('Enter discount percent')]); ?> </div> <div class="form-group<?php echo e($errors->has('discount_amount') ? ' has-error' : ''); ?>" id="discount_flat_rate"> <?php echo Form::label('discount_amount', __('Discount Amount:')); ?> <?php echo Form::number('discount_amount', null, ['class'=>'form-control', 'id'=>'discount_amount', 'placeholder'=>__('Enter discount amount')]); ?> </div> <div class="form-group<?php echo e($errors->has('use_coupon_one_time') ? ' has-error' : ''); ?>" id=""> <?php echo Form::label('use_coupon_one_time', __('One time use :')); ?> <?php echo Form::checkbox('use_coupon_one_time', 1, false); ?> </div> <div class="form-group<?php echo e($errors->has('starts_at') ? ' has-error' : ''); ?>"> <?php echo Form::label('starts_at', __('Starting Date and Time:')); ?> <?php echo Form::text('starts_at', null, ['class'=>'form-control', 'placeholder'=>__('Select starting date and time'), 'required', 'id' =>'datetimepicker_start']); ?> </div> <div class="form-group<?php echo e($errors->has('expires_at') ? ' has-error' : ''); ?>"> <?php echo Form::label('expires_at', __('Ending Date and Time:')); ?> <?php echo Form::text('expires_at', null, ['class'=>'form-control', 'placeholder'=>__('Select ending date and time'), 'required', 'id' =>'datetimepicker_end']); ?> </div> <div class="form-group<?php echo e($errors->has('max_uses_user') ? ' has-error' : ''); ?>"> <?php echo Form::label('valid_above_amount', __('Amount above which Coupon is Valid:')); ?> <?php echo Form::number('valid_above_amount', null, ['class'=>'form-control', 'step'=>'any', 'placeholder'=>__('Enter amount above which coupon is valid'), 'min'=>0]); ?> </div> <div class="form-group"> <?php echo Form::submit(__('Add Coupon'), ['class'=>'btn btn-primary btn-block', 'name'=>'submit_button']); ?> </div> <?php echo Form::close(); ?> </div> </div>