<div class="panel panel-default"> <div class="panel-heading"> <?php echo app('translator')->getFromJson('View Categories'); ?> </div> <div class="panel-body"> <form id="delete-form" action="delete/categories" method="post" class="form-inline"> <div class="row"> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Category::class)): ?> <?php echo e(csrf_field()); ?> <div class="col-md-4"> <div class="text-muted"> <label for="checkboxArray"><?php echo app('translator')->getFromJson('Bulk Options'); ?> <i class="fa fa-cog" aria-hidden="true"></i></label> </div> <input type="hidden" name="_method" value="DELETE"> <div class="form-group"> <select name="checkboxArray" class="form-control"> <option value=""><?php echo app('translator')->getFromJson('Delete'); ?></option> </select> </div> <div class="form-group"> <input id="delete_all" name="" class="btn fa btn-warning" value="&#xf1d8;" onclick=" if(confirm('<?php echo app('translator')->getFromJson('Are you sure you want to delete selected categories?'); ?>')) { $('#delete_all').attr('name', 'delete_all'); event.preventDefault(); $('#delete-form').submit(); } else { event.preventDefault(); } " > </div> </div> <?php endif; ?> </div> <div class="table-responsive"> <table class="display table table-striped table-bordered table-hover" id="categories-table"> <thead> <tr> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Category::class)): ?> <th><input type="checkbox" id="options"></th> <?php endif; ?> <th><?php echo app('translator')->getFromJson('Name'); ?></th> <th><?php echo app('translator')->getFromJson('Parent Category'); ?></th> <th><?php echo app('translator')->getFromJson('Status'); ?></th> <th><?php echo app('translator')->getFromJson('Created'); ?></th> <?php if((Auth::user()->can('update', App\Category::class)) || (Auth::user()->can('delete', App\Category::class))): ?> <th><?php echo app('translator')->getFromJson('Action'); ?></th> <?php endif; ?> </tr> </thead> <tbody> <?php if($categories): ?> <?php $__currentLoopData = $categories; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $category): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?> <tr> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Category::class)): ?> <td><input class="checkboxes" type="checkbox" name="checkboxArray[]" value="<?php echo e($category->id); ?>"></td> <?php endif; ?> <td><?php echo e($category->name); ?></td> <td><?php echo e($category->category ? $category->category->name : 'None'); ?></td> <td><?php echo e($category->is_active ? __('Active') : __('Inactive')); ?></td> <td><?php echo e($category->created_at); ?></td> <?php if((Auth::user()->can('update', App\Category::class)) || (Auth::user()->can('delete', App\Category::class))): ?> <td> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('update', App\Category::class)): ?> <a class="btn btn-info btn-sm" href="<?php echo e(route('manage.categories.edit', $category->id)); ?>"> <i class="fa fa-pencil"></i> </a> <?php endif; ?> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Category::class)): ?> <input type="hidden" id="delete_single" name=""> <a class="btn btn-danger btn-sm" href="" onclick=" if(confirm('<?php echo app('translator')->getFromJson('Are you sure you want to delete this?'); ?>')) { $('#delete_single').attr('name', 'delete_single').val(<?php echo e($category->id); ?>); event.preventDefault(); $('#delete-form').submit(); } else { event.preventDefault(); } " ><i class="fa fa-trash-o"></i></a> <?php endif; ?> </td> <?php endif; ?> </tr> <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?> <?php endif; ?> </tbody> </table> </div> </form> </div> </div>