<div class="row"> <div class="col-xs-12 col-sm-8"> <?php echo $__env->make('includes.form_errors', array_except(get_defined_vars(), array('__data', '__path')))->render(); ?> <?php echo Form::open(['method'=>'post', 'action'=>'ManageTestimonialsController@store', 'files'=>true, 'onsubmit'=>'submit_button.disabled = true; submit_button.value = "' . __('Please Wait...') . '"; return true;']); ?> <div class="form-group<?php echo e($errors->has('author') ? ' has-error' : ''); ?>"> <?php echo Form::label('author', __('Author:')); ?><em style="color:red;">*</em> <?php echo Form::text('author', null, ['class'=>'form-control', 'placeholder'=>__("Enter author's name"), 'required']); ?> </div> <div class="form-group<?php echo e($errors->has('review') ? ' has-error' : ''); ?>"> <?php echo Form::label('review', __('Review:')); ?><em style="color:red;">*</em> <?php echo Form::textarea('review', null, ['class'=>'form-control', 'placeholder'=>__("Enter author's review"), 'required']); ?> </div> <div class="form-group"> <?php echo Form::label('status', __('Status:')); ?> <?php echo Form::select('status', [0=>__('inactive'), 1=>__('active')], 1, ['class'=>'form-control selectpicker', 'data-style'=>'btn-default']); ?> </div> <div class="form-group"> <?php echo Form::label('photo', __('Choose photo'), ['class'=>'btn btn-default btn-file']); ?> <?php echo Form::file('photo',['class'=>'form-control', 'style'=>'display: none;','onchange'=>'$("#upload-file-info").html(files[0].name)']); ?> <span class='label label-info' id="upload-file-info"><?php echo app('translator')->getFromJson('No photo chosen'); ?></span> </div> <div class="form-group"> <?php echo Form::submit(__('Add Testimonial'), ['class'=>'btn btn-primary btn-block', 'name'=>'submit_button']); ?> </div> <?php echo Form::close(); ?> </div> </div>