<div class="panel panel-default"> <div class="panel-heading"> <?php echo app('translator')->getFromJson('View Testimonials'); ?> </div> <div class="panel-body"> <form id="delete-form" action="delete/testimonials" method="post" class="form-inline"> <div class="row"> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Testimonial::class)): ?> <?php echo e(csrf_field()); ?> <div class="col-md-4"> <div class="text-muted"> <label for="checkboxArray"><?php echo app('translator')->getFromJson('Bulk Options'); ?> <i class="fa fa-cog" aria-hidden="true"></i></label> </div> <input type="hidden" name="_method" value="DELETE"> <div class="form-group"> <select name="checkboxArray" class="form-control"> <option value=""><?php echo app('translator')->getFromJson('Delete'); ?></option> </select> </div> <div class="form-group"> <input id="delete_all" name="" class="btn fa btn-warning" value="&#xf1d8;" onclick=" if(confirm('<?php echo app('translator')->getFromJson('Are you sure you want to delete selected testimonials?'); ?>')) { $('#delete_all').attr('name', 'delete_all'); event.preventDefault(); $('#delete-form').submit(); } else { event.preventDefault(); } " > </div> </div> <?php endif; ?> <div class="advanced-search col-md-<?php echo e(Auth::user()->can('delete', App\Testimonial::class) ? '8' : '8 col-md-offset-4'); ?>"> </div> </div> <div class="table-responsive"> <table class="display table table-striped table-bordered table-hover" id="testimonials-table"> <thead> <tr> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Testimonial::class)): ?> <th><input type="checkbox" id="options"></th> <?php endif; ?> <th><?php echo app('translator')->getFromJson('Photo'); ?></th> <th><?php echo app('translator')->getFromJson('Author'); ?></th> <th><?php echo app('translator')->getFromJson('Review'); ?></th> <th><?php echo app('translator')->getFromJson('Status'); ?></th> <th><?php echo app('translator')->getFromJson('Created'); ?></th> <th width="60px;"><?php echo app('translator')->getFromJson('Action'); ?></th> </tr> </thead> <tbody> <?php if($testimonials): ?> <?php $__currentLoopData = $testimonials; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $testimonial): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?> <tr> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Testimonial::class)): ?> <td><input class="checkboxes" type="checkbox" name="checkboxArray[]" value="<?php echo e($testimonial->id); ?>"></td> <?php endif; ?> <td> <?php if($testimonial->photo): ?> <?php
                                            $image_url = \App\Helpers\Helper::check_image_avatar($testimonial->photo->name, 50);
                                        ?> <img src="<?php echo e($image_url); ?>" height="50px" alt="<?php echo e($testimonial->author); ?>" /> <?php else: ?> <img src="https://via.placeholder.com/50x50?text=No+Image" height="50px" alt="<?php echo e($testimonial->author); ?>" /> <?php endif; ?> </td> <td><?php echo e($testimonial->author); ?></td> <td><?php echo e($testimonial->review); ?></td> <td> <?php if($testimonial->is_active == '1'): ?> <span class="badge bg-success"><i class="fa fa-check"></i></span> <?php else: ?> <span class="badge bg-danger"><i class="fa fa-times"></i></span> <?php endif; ?> </td> <td><?php echo e($testimonial->created_at); ?></td> <td> <?php if((Auth::user()->can('update', App\Testimonial::class)) || (Auth::user()->can('delete', App\Testimonial::class))): ?> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('update', App\Testimonial::class)): ?> <a class="btn btn-info btn-sm" href="<?php echo e(route('manage.testimonials.edit', $testimonial->id)); ?>"> <i class="fa fa-pencil"></i> </a> <?php endif; ?> <?php if (app(\Illuminate\Contracts\Auth\Access\Gate::class)->check('delete', App\Testimonial::class)): ?> <input type="hidden" id="delete_single" name=""> <a class="btn btn-danger btn-sm" href="" onclick=" if(confirm('<?php echo app('translator')->getFromJson('Are you sure you want to delete this?'); ?>')) { $('#delete_single').attr('name', 'delete_single').val(<?php echo e($testimonial->id); ?>); event.preventDefault(); $('#delete-form').submit(); } else { event.preventDefault(); } " > <i class="fa fa-trash-o"></i> </a> <?php endif; ?> <?php endif; ?> </td> </tr> <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?> <?php endif; ?> </tbody> </table> </div> </form> </div> </div>